import React from "react";
import "../css/akademii.css";
import data from "../akademii.json";
import images from "../images";
const AkademiiCards = () => {
  return (
    <div className="academii-crds">
      {data.map((card) => (
        <div
          className="cardAcademii"
        //   style={{
        //     backgroundImage: `url("../images/${card.image}.jpg")`,
        //     backgroundPosition: "center",
        //     backgroundSize: "cover",
        //     borderRadius: "5px"
        //   }}
        >
          <div
            style={{
              backgroundColor: `rgba(${card.bcolor})`,
              width: "100%",
              height: "100%",
              borderRadius: "5px",
              padding: "15px"
            }}
          >
            <img src={require(`images.${card.image}.jpg`)}></img>
            <h1>{card.title}</h1>
            <p>{card.mesta}</p>
            <p>{card.upisi}</p>
            <p>{card.postani}</p>
            <p>{card.partneri}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AkademiiCards;
