import React from "react";
import blogData from "../blog.json";
import images from "../images";
import "../css/cadr.css";
const BlogCards = () => {
  return (
    <div className="all-cards">
      {blogData.map((item) => (
        <div className="one-card">
            <div className="image-div" style={{"width": "100%","height": "250px"}}><img src={require(`../images${blogData.image}.jpg`)}></img></div>
            {/* <div className="image-div" style={{"width": "100%","height": "250px", "backgroundColor": "orange"}}></div> */}
          <h1>{item.title}</h1>
          <p>{item.text}</p>
        </div>
      ))}
    </div>
  );
};

export default BlogCards;
